def interface_return(func):
    """
    统一接口函数的返回值
    :param func:
    :return:
    """

    @wraps(func)
    def wrapper(*args, **kwargs):
        response = http.Response(content_type="application/json")
        try:
            func_res = func(*args, **kwargs)
            res = {
                "msg": "success",
            }
            if func_res is not None:
                res["data"] = func_res
            response.set_data(json.dumps(res))
        except InterfaceError as ie:
            response.set_data(json.dumps({
                "code": ie.code,
                "result": "fail",
                "msg": ie.msg
            }))
        except Exception as e:
            response.set_data(json.dumps({
                "result": "fail",
                "msg": str(e)
            }))
        return response

    return wrapper

