@api.multi
def view_$order_name$_orders(self, orders,action_xmlid):
    u"""
    查看单据，如果是单条记录、或者是多条记录，默认显示form、或者tree视图。
    :return: dict,临时的一个odoo action
    """
    self.ensure_one()
    if orders:
        action = self.env.ref(action_xmlid)
        action_vals = action.read()[0]
        context = safe_eval(action_vals.get("context", "{}"))
        context.update({
            # "create": False,
            # "delete": False,
        })
        action_vals["context"] = context
        # 如果只是多个单据，则跳转到这个单据的tree视图，用domain过滤想到显示的记录。
        if len(orders) > 1:
            domain = eval(action_vals["domain"]) if action_vals["domain"] else []
            domain.append(("id", "in", orders.ids))
            action_vals["domain"] = domain
        # 如果只是一个单据，则跳转到这个单据的form视图。
        elif len(orders) == 1:
            action_vals["view_mode"] = "form"
            action_vals["views"] = [view for view in action_vals["views"] if view[1] == u"form"]
            action_vals["res_id"] = orders.id
        else:
            action_vals = {'type': 'ir.actions.act_window_close'}
        return action_vals
    else:
        return True

def interface_return(func):
    """
    统一接口函数的返回值
    :param func:
    :return:
    """

    @wraps(func)
    def wrapper(*args, **kwargs):
        response = Response()
        try:
            func_res = func(*args, **kwargs)
            res = {
                "result": "success",
            }
            if func_res is not None:
                res["data"] = func_res
            response.set_data(json.dumps(res))
        except Exception as e:
            response.set_data(json.dumps({
                "result": "fail",
                "errmsg": str(e)
            }))
        return response

    return wrapper
